  font-family: "Tele-GroteskFet", "Tele-Fet", "Tele-Grotesk-Next-Medium", Sans-Serif;
}
#PrimaryCTALP p.bub-text-pri-normal {
  font-family: BlinkMacSystemFont, -apple-system, Segoe UI, Roboto, Helvetica, Arial, sans-serif;
}
.chat-fixed-container-pri,
.chat-fixed-container-pri > div {
  -webkit-box-sizing: border-box;
  box-sizing: border-box;
}
.chat-fixed-container-pri {
  -webkit-transition: all 0.5s cubic-bezier(0, 1.19, 1, 1.12);
  -o-transition: all 0.5s cubic-bezier(0, 1.19, 1, 1.12);
  transition: all 0.5s cubic-bezier(0, 1.19, 1, 1.12);
  -webkit-transition-delay: 0.75s;
  -o-transition-delay: 0.75s;
  transition-delay: 0.75s;
  -webkit-box-sizing: border-box;
  box-sizing: border-box;
}
.chat-fixed-container-pri.show {
  right: 0;
}
.chat-fixed-container-pri.peak {
  right: 20px;
}
.chat-container-pri {
  position: absolute;
  bottom: 0px;
  right: 10px;
}
.chat-container-avoid-arrow-pri {
  position: absolute;
  bottom: 48px;
  right: 10px;
}
.chat-dismiss-pri {
  background-size: 50%;
  background: url("https://www.t-mobile.com/content/dam/t-mobile/ntm/branding/icons/iconography/dismiss.svg") center center no-repeat;
  border-radius: 100%;
  color: #fff;
  cursor: pointer;
  display: block;
  height: 40px;
  position: absolute;
  right: -5px;
  top: -41px;
  -webkit-transition: -webkit-transform 0.5s ease;
  transition: -webkit-transform 0.5s ease;
  -o-transition: transform 0.5s ease;
  transition: transform 0.5s ease;
  transition: transform 0.5s ease, -webkit-transform 0.5s ease;
  width: 40px;
}
@media only screen and (min-width: 728px) {
  .chat-bubble-pri:hover {
    -webkit-box-shadow: 0px 8px 25px rgba(227, 0, 118, 0.44);
    box-shadow: 0px 8px 25px rgba(227, 0, 118, 0.44);
  }
  .chat-dismiss-pri:hover {
    -webkit-transform: scale(1.2);
    -ms-transform: scale(1.2);
    transform: scale(1.2);
    -webkit-transition: -webkit-transform 0.1s ease;
    transition: -webkit-transform 0.1s ease;
    -o-transition: transform 0.1s ease;
    transition: transform 0.1s ease;
    transition: transform 0.1s ease, -webkit-transform 0.1s ease;
  }
}
.chat-dismiss-pri:active {
  -webkit-transform: scale(1.4);
  -ms-transform: scale(1.4);
  transform: scale(1.4);
}
.chat-dismiss-pri.small {
  -webkit-transform: scale(0);
  -ms-transform: scale(0);
  transform: scale(0);
}
.chat-bubble-pri,
.chat-bubble-pri-sp {
  background-image: -webkit-gradient(linear, left top, right top, color-stop(0, #b853bc), to(#e20074));
  background-image: -o-linear-gradient(left, #b853bc 0, #e20074 100%);
  background-image: linear-gradient(90deg, #b853bc 0, #e20074 100%);
  -webkit-box-shadow: 0 2px 13px 0 rgba(0, 0, 0, 0.16);
  box-shadow: 0 2px 13px 0 rgba(0, 0, 0, 0.16);
  background-size: 100%;
  background-color: #be48b3;
  height: 48px;
  border-radius: 24px;
  padding: 0 17px 0 52px;
  position: relative;
  z-index: 2;
  cursor: pointer;
  -webkit-box-sizing: border-box;
  box-sizing: border-box;
}
.chat-bubble-pri {
  min-width: 210px;
}
.chat-bubble-pri-sp-width {
  min-width: 150px;
}
.chat-bubble-pri.play-pri {
  -webkit-animation-name: bubResize-pri;
  animation-name: bubResize-pri;
  -webkit-animation-duration: 30s;
  animation-duration: 30s;
  -webkit-animation-timing-function: ease-in;
  animation-timing-function: ease-in;
  -webkit-animation-fill-mode: forwards;
  animation-fill-mode: forwards;
}
.chat-bubble-pri.chat-bubble-pri-sp-width.play-pri {
  -webkit-animation-name: bubResize-pri-sp;
  animation-name: bubResize-pri-sp;
}
.chat-fixed-container-pri.peak .chat-bubble-pri {
