.dots-pri {
  position: absolute;
  top: 15px;
  left: 30px;
  height: 20px;
  width: 80px;
  -webkit-transform: scale(0.5);
  -ms-transform: scale(0.5);
  transform: scale(0.5);
  opacity: 0;
  -webkit-animation-name: dotsDisplay-pri;
  animation-name: dotsDisplay-pri;
  -webkit-animation-duration: 30s;
  animation-duration: 30s;
  -webkit-animation-timing-function: ease-in;
  animation-timing-function: ease-in;
  -webkit-animation-fill-mode: forwards;
  animation-fill-mode: forwards;
}
.dots-pri .dot-pri {
  position: absolute;
  display: block;
  top: 0;
  height: 20px;
  width: 20px;
  border-radius: 50%;
  background-color: #eee;
}
.dots-pri .dot-pri:nth-child(1) {
  left: 0;
  -webkit-animation-name: hoppy-pri;
  animation-name: hoppy-pri;
  -webkit-animation-duration: 1.1s;
  animation-duration: 1.1s;
  -webkit-animation-timing-function: ease-in;
  animation-timing-function: ease-in;
  -webkit-animation-iteration-count: infinite;
  animation-iteration-count: infinite;
}
.dots-pri .dot-pri:nth-child(2) {
  left: 30px;
  -webkit-animation-name: hoppy-pri;
  animation-name: hoppy-pri;
  -webkit-animation-duration: 1.1s;
  animation-duration: 1.1s;
  -webkit-animation-delay: 150ms;
  animation-delay: 150ms;
  -webkit-animation-timing-function: ease-in;
  animation-timing-function: ease-in;
  -webkit-animation-iteration-count: infinite;
  animation-iteration-count: infinite;
}
.dots-pri .dot-pri:nth-child(3) {
  left: 60px;
  -webkit-animation-name: hoppy-pri;
  animation-name: hoppy-pri;
  -webkit-animation-duration: 1.1s;
  animation-duration: 1.1s;
  -webkit-animation-delay: 0.3s;
  animation-delay: 0.3s;
  -webkit-animation-timing-function: ease-in;
  animation-timing-function: ease-in;
  -webkit-animation-iteration-count: infinite;
  animation-iteration-count: infinite;
}
@-webkit-keyframes hoppy-pri {
  0% {
    -webkit-transform: translateY(0);
    transform: translateY(0);
    opacity: 20;
  }
  40% {
    -webkit-transform: translateY(-10px);
    transform: translateY(-10px);
    opacity: 0.13;
  }
  60% {
    -webkit-transform: translateY(0);
    transform: translateY(0);
    opacity: 0.5;
  }
  100% {
    -webkit-transform: translateY(0);
    transform: translateY(0);
    opacity: 0.5;
  }
}
@keyframes hoppy-pri {
