/* ANIMATED TMO BUTTON */
.chat-fixed-container {
  position: fixed;
  bottom: 0;
  transition: all 0.5s cubic-bezier(0, 1.19, 1, 1.12);
  transition-delay: 0.25s;
  box-sizing: border-box;
}
.chat-fixed-container.show {
  right: 0;
  z-index: 800;
}
.chat-fixed-container.peak {
  right: 20px;
}
.chat-container {
  position: fixed;
  bottom: 22px;
  right: 22px;
}
.chat-bubble {
  background-image: linear-gradient(90deg, #b853bc 0, #e20074 100%);
  box-shadow: 0 2px 13px 0 rgba(0, 0, 0, 0.16);
  background-size: 100%;
  background-color: #be48b3;
  min-width: 230px;
  height: 48px;
  border-radius: 24px;
  padding: 0 17px 0 52px;
  position: relative;
  z-index: 2;
  cursor: pointer;
  box-sizing: border-box;
}
.chat-fixed-container.peak .chat-bubble {
  min-width: 230px;
}
.chat-bubble:after {
  content: '';
  background: url('https://static-assets.fs.liveperson.com/tmo/images/animatedBtn/chat-tip.svg');
  position: absolute;
  display: block;
  bottom: -11px;
  left: 20px;
  height: 20px;
  width: 18px;
  z-index: 0;
}
.bub-text-container {
  display: block;
  overflow-y: hidden;
  height: 100%;
  width: 100%;
  overflow: none;
}
.bub-text {
  font-weight: 900 !important;
  font-size: 18px !important;
  color: #fff !important;
  line-height: 48px !important;
  letter-spacing: 0.03em !important;
  margin: 0 !important;
  padding: 0 !important;
}
.bub-text img {
  vertical-align: middle;
}
.chat-copy {
  animation-name: chat1;
  animation-duration: 1s;
  animation-timing-function: ease-in;
  animation-fill-mode: forwards;
  animation-delay: 4s;
}
.chat-copy2 {
  transform: translateY(0);
  animation-name: chatUp;
  animation-duration: 7s;
  animation-timing-function: ease-in;
  animation-fill-mode: forwards;
  animation-delay: 12s;
}
.chat-copy3 {
  transform: translateY(0);
  animation-name: chatUp2;
  animation-duration: 7s;
  animation-timing-function: ease-in;
  animation-fill-mode: forwards;
  animation-delay: 21s;
}
.chat-bubble:before {
  box-shadow: inset 0 0 3px rgba(0, 0, 0, 0.25);
  height: 25px;
  width: 25px;
  display: block;
}
